int _write(int file, char *ptr, int len)
{

	extern UART_HandleTypeDef huart2;

	HAL_StatusTypeDef result = HAL_OK;

	result = HAL_UART_Transmit(&huart2, (uint8_t *)ptr, len, HAL_MAX_DELAY);
	if(result == HAL_ERROR || result == HAL_BUSY)
	{
		Error_Handler();
	}
	return len;
}

